var myModule = require('../../../app.json');
var sitename = myModule.sitename
var website = myModule.website
var description = myModule.description
var repo = myModule.repo
var ObjectId = require('mongodb').ObjectID;

var wrasseModel = require('../models/wrasse.js'); 

///////////////////////////////////////////////
///////   DELETE AN ITEM BY ID   ////////
/////////////////////////////////////////////
exports.deleteitem = function(req, res,next) { 
	var receivedid = req.params['deleteid']
	wrasseModel.remove({"_id":ObjectId(receivedid)}, function(err){
		if(err){console.log(err, "this error here")
			req.wrassemessage = '<span class="badge badge-fail"><i class="fas fa-exclamation-circle"></i> Closed</span> Issue not found.'
		next();
	}
	req.wrassemessage = '<i class="far fa-trash-alt"></i> Deleted</span> Issue successfully deleted.'
	next();
})
};
