exports.payment_notify = function(req, res) {
  //Edit the user database to log plan change.
  if (req.body.m_payment_id) {
    if (req.body.payment_status == 'COMPLETE') {
      organizationalModel.findById(req.body.m_payment_id, function(err, org) {
        if (org) {
          var temp = JSON.parse(JSON.stringify(org.entry))
          temp.plan = {}
          temp.plan.payfast = req.body
          temp.plan.business_billing_name = req.body.custom_str2
          org.entry = temp 
          org.save(function(err) {
          var subject= '✔ New Organization Subscription on '+sitename // Subject line
          var html =  'Congrats a new organization has subscribed! <pre>'+JSON.stringify(req.body)+'</pre>'
          email(subject,html)
        });
        } else {
          console.log('Error here hacking ??')
        }
      });
    }
    if (req.body.payment_status == 'CANCELLED') {
      organizationalModel.findById(req.body.m_payment_id, function(err, org) {
        if (org) {
          var temp = JSON.parse(JSON.stringify(org.entry))
          temp.plan = {}
          temp.plan.payfast = req.body
          org.entry = temp 
          org.save(function(err) {
          var subject= '× Cancelled organization Subscription on '+sitename // Subject line
          var html =  'An existing organization has cancelled their subscription! <pre>'+JSON.stringify(req.body)+'</pre>'
          email(subject,html)
        });
        } else {
          console.log('Error here hacking ??')
        }
      });
    }
  } 
}; 

function email(subject,html){
  //send email notification admin.
  var port = process.env.MAIL_PORT
  var useremail = process.env.MAIL_USERNAME
  var passwords = process.env.MAIL_PASSWORD
  var host = process.env.MAIL_HOST
  var temp = {}
  'use strict';
  var nodemailer = require('nodemailer');
// create reusable transporter object using the default SMTP transport
var transporter = nodemailer.createTransport({
  host: host,
  tls: {
    rejectUnauthorized: false
  },
secure: false, // secure:true for port 465, secure:false for port 587
auth: {
  user: useremail,
  pass: passwords,
}
}); 
var emailofUSer = 'test@test.com'
var nameofUSer = 'Test Here'
var mailOptions = {
from: 'The '+sitename+' Team' + ' ' + '<'+ process.env.MAIL_USERNAME + '>', // sender address
to: process.env.MAIL_USERNAME , // list of receivers
subject: subject, // Subject line
html:  html,
}
transporter.sendMail(mailOptions, (error, info) => {
  if (error) {
    return console.log(error);
  }
  console.log('Message %s sent: %s', info.messageId, info.response);
  return
});
}
